import React, {Component} from 'react';
import CryptoList from './Cryptolist';

import axios from 'axios';

class Input extends Component {

    constructor() {
        super();

        this.state = {
            currencyList: []
        }
    };

    getCurrencyData = () => {
        console.log('pobieram');
        axios.get(`https://blockchain.info/pl/ticker`)
            .then(res => {
                const currencyList = res.data;

                let currentArray = [];
                let i = 0;
                    for( let key in currencyList) {

                        let newCurrency = currencyList[key];
                        let prevCurrency = this.state.currencyList[i];
                        console.log(prevCurrency);

                        newCurrency.currency = key;
                        currentArray.push(currencyList[key]);
                        i++;
                    }
                    this.setState({currencyList: currentArray});
                })
    }

    componentDidMount() {
        this.getCurrencyData();

        this.timer = setInterval(this.getCurrencyData, 5000);

    };

    render() {
        return(
        <div className="input">
            <input className="input-element" type="text" placeholder="Wpisz kraj" />
        </div>,
        <CryptoList cryptoArray={this.state.currencyList}/>
        )}

    }

export default Input;
