import React, {Component} from 'react';

import axios from 'axios';
import CountriesList from './Countries_list';

class Input extends Component {

    constructor() {
        super();

        this.state = {
            currencyList: []
        }
    };

    componentDidMount() {
        axios.get(`https://blockchain.info/pl/ticker`)
            .then(res => {
                const currencyList = res.data;

                let currentArray = [];

                    for( let key in currencyList) {

                        currencyList[key].currency = key;
                        currentArray.push(currencyList[key]);
                    }
                    console.log(currentArray);
                    this.setState({currencyList: currentArray})
                })

    };

    render() {
        return(
        <div className="input">
            <input className="input-element" type="text" placeholder="Wpisz kraj" />
        </div>,
        <CountriesList abc={this.state.currentArray}/>
        )};

    }

export default Input;
