// import CountriesList from './Countries_list';

import axios from 'axios';

export default class Input extends Component {

    constructor() {
        super();

        this.state = {
            array: []
        }
    }

    componentDidMount() {
        axios.get(`https://blockchain.info/pl/ticker`)
            .then(res => {
                const array = res.data;

                let currentArray = [];

                    for( let key in array) {

                        array[key].currency = key;
                        currentArray.push(array[key]);
                    }
                    console.log(currentArray);



                })

    }

    render() {
        return(
        <div className="input">
            <input className="input-element" type="text" placeholder="Wpisz kraj" />
        </div>,
        <div className="countries">
            <ul className="currency">
                {/* <CountriesList info={this.state.array}/> */}
            </ul>
        </div>
        )}
}
